#include <iostream>
#include <string>

bool isSubsequence(std::string s, std::string t) {
    // std::cout << "isSubsequence(" << s << ", " << t << ")" << std::endl;
    if (t.length() < s.length()) {
        return false;
    } else if (s.length() == 0) {
        return true;
    } else {
        std::size_t pos = t.find(s[0]);
        if (pos != std::string::npos) {
            std::cout << "s: " << s << ", pos: " << pos << std::endl;
            return isSubsequence(s.substr(1), t.substr(pos + 1));
        } else {
            return false;
        }
    }
}

int main() {
    std::string s = "abc";
    std::string s2 = "axc";
    std::string t = "ahbdgc";
    std::string s3 = "leeeeetcode";
    std::string t2 = "";
    std::cout << isSubsequence(s3, t2);
    return 0;
}
